package introduccion;import java.util.Scanner;//Pedir 3 valores y calcular la media aritm√©ticapublic class PedirValoresCalcularMedia {    public static void main(String[] args) {        Scanner teclado = new Scanner(System.in);        //Entrada de datos        System.out.println("Introduce 3 valores: ");        double numeroUno = teclado.nextDouble();        double numeroDos = teclado.nextDouble();        double numeroTres = teclado.nextDouble();        //Algoritmo        //Sumar los tres valores y dividir entre tres        double media = (numeroUno + numeroDos + numeroTres) / 3;        //Salida de datos        System.out.println("La media es: " + media);        }    }